DROP TABLE IF EXISTS audio_data CASCADE;

CREATE TABLE audio_data (
    audio_id INT GENERATED ALWAYS AS IDENTITY,
    audio_name VARCHAR(200),
    audio_content BYTEA,
    text VARCHAR(100),
    topic VARCHAR(100)
);

